export function initVersion(){fetch("./config/site.config.js").then(t=>t.text()).then(t=>{const n=t.match(/version:\s*['"]([^'"]+)['"]/);if(n&&n[1]){updateVersionDisplay(n[1])}}).catch(t=>{console.warn("Could not load version from config:",t)})}function updateVersionDisplay(t){const n=document.getElementById("site-version");if(n){const o=t.startsWith("v")?t:`v${t}`;n.textContent=o}}